import React, { Component } from "react";
import Table from "react-bootstrap/Table";
import NavBar from "./leftbar";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { Route } from "react-router-dom";
import axios from "axios";
import { Redirect } from "react-router-dom";
import swal from "sweetalert";
import ListGroup from "react-bootstrap/ListGroup";

class GetQuestions extends Component {
  constructor(props) {
    super(props);
    this.state = {
      skills: [],
      answers: [],
      email: "",
      selectedOption : "1",
      radio:[]
    };
  }
  componentDidMount() {
    const { result } = this.props.location.state;
    console.log("passed value is ", result);
    let currentComponent = this;
    currentComponent.setState({
      pname: result.pname,
      desc: result.desc,
      skills: result.skills,
      jobid: result.jobid
    });
    let email = result.email;
    this.state.email = email;
    let data = {
      email: email,
      intervieweremail: localStorage.getItem("email")
    };
    //console.log("jobid is ", jobid);
    let url = "http://localhost:3001/api/feedback/getQuestions";
    /* axios
      .get(url, data)
      .then(response => {
        console.log("response is..........", response.data);
        this.setState({
          skills: this.state.candidates.concat(response.data.skills)
        });
      })
      .catch(err => console.log(err));*/
    let response = {
      skills:["Java","MongoDB","Kafka","React"]
    };
    this.setState({
      skills: this.state.skills.concat(response.skills)
    });
    let radio = []
    let i=1;
    let options
    let questions_answers = response.skills.map(skills => {

      options = []
      i = 1;
      while(i < 6){
          let data={
            value: i
          };
        options.push(data);
        i=i+1
      }
      let merge = {
        skills: skills,
        options: options
      }
      radio.push(merge);
    });
    console.log(radio);
    this.setState({
      radio: radio
    });
  }

  updateAnswers(skills, value){
    let answers = this.state.answers;
    let i = -1;
    let flag = 0;
    let an = this.state.answers.map(answers => {
      i = i+1;
      if(answers.skills == skills){
        this.state.answers[i].value = value;
        flag = 1;
      }
    });
    let data={}
    if(flag == 0){
      let data = {
        skills:skills,
        value: value
      }
      this.state.answers.push(data);
    }
    console.log(this.state.answers)
  }

  onInputChange = (e) => {
    let change = this.state.radio;
    let checked = false;
    let nexState = change.map(change => {
      if (change.skills !== e.target.name) return change;
      return {
        ...change,
        options: change.options.map(opt => {
          checked = opt.value === Number(e.target.value);
          if(checked == true){
            console.log(e.target.name)
            console.log(e.target.value)
            this.updateAnswers(e.target.name, e.target.value);
          }
          return {
            ...opt,
            selected: checked
          }

        })
      }
    });
    this.setState({ radio: nexState })
    console.log(this.state.radio);
  }

  handleOptionChange = (changeEvent) => {
    let details_completed = this.state.answers.map(answers => {
      if(answers.skills == changeEvent.target.row){
        answers.value = changeEvent.target.value
        return
      }
    });
    let data = {
      skills: changeEvent.target.row,
      value: changeEvent.target.value
    }
    console.log(changeEvent.target.row)
    this.setState({
      answers: this.state.answers.concat(data),
      selectedOption: changeEvent.target.value
    });
  }

  submitForm = (changeEvent) => {
    let url = "http://localhost:3001/api/feedback/generateFeedbackReport";
    let data = {
      email: this.state.email,
      answers: this.state.answers
    }
    console.log(data)
    /* axios
      .get(url, data)
      .then(response => {
        console.log("response is..........", response.data);

      })
      .catch(err => console.log(err));*/
  }

  render() {
    let skills = this.state.skills;
    let redirectVar = null;
    let radio = this.state.radio;
    return (
      <div>
        {" "}
        {redirectVar}
        <NavBar />
        <br />
        <br />
        <br />
        <br />
        <Table borderless>
          <tr>
            <td>
              <ListGroup variant="flush" style={group}>
                <ListGroup.Item action href="./homeInterviewer">
                  <p style={para}> Home </p>
                </ListGroup.Item>
              </ListGroup>
            </td>
            <td style={para} width="700">
        <Table bordered>
          <tr>
            <td width="100%">Please rate the candidate on the basis of following skills:
            <br/>Rate from 1-5
            <br/>1 -> Not applicable
            <br/>2 -> Does not meet requirement
            <br/>3 -> Can Learn with Training
            <br/>4 -> Meets Requirements
            <br/>5 -> Exceeds Expectations</td>
          </tr>

        <div>
        {
          radio.length < 1 ? "Loading..." :
            <div>
              {radio.map((card, idx) => (
                <ul>
                  {card.skills}
                  {
                    card.options.map((lo, idx) => {
                        return <input
                        key={idx}
                        type="radio"
                        name={card.skills}
                        value={lo.value}
                        onChange={this.onInputChange}/>
                    })
                  }
                </ul>
              ))
              }

            </div>
          }
          </div>
        <Button
                  variant="light"
                  style={btn}
                  onClick={this.submitForm}
                >
                  Submit Form
                </Button>
                </Table>
            </td>
          </tr>
        </Table>
      </div>
    );
  }
}
const group = {
  width: 320,
  height: 700
};
const para = {
  fontfamily: "Arial, Helvetica, sans-serif",
  fontSize: 30,
  textAlign: "center",
  padding: 10,
  margin: 10,
  display: "inline-block",
  verticalAllign: "middle"
};
