import React, { Component } from "react";
import Table from "react-bootstrap/Table";
import NavBar from "./leftbar";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { Route } from "react-router-dom";
import axios from "axios";
import { Redirect } from "react-router-dom";
import swal from "sweetalert";
import ListGroup from "react-bootstrap/ListGroup";

class GetQuestions extends Component {
  constructor(props) {
    super(props);
    this.state = {
      skills: [],
      answers: [],
      selectedOption : "1"
    };
  }
  componentDidMount() {
    const { result } = this.props.location.state;
    console.log("passed value is ", result);
    let currentComponent = this;
    currentComponent.setState({
      pname: result.pname,
      desc: result.desc,
      skills: result.skills,
      jobid: result.jobid
    });
    let email = result.email;
    let data = {
      email: email,
      intervieweremail: localStorage.getItem("email")
    };
    //console.log("jobid is ", jobid);
    let url = "http://localhost:3001/api/feedback/getQuestions";
    /* axios
      .get(url, data)
      .then(response => {
        console.log("response is..........", response.data);
        this.setState({
          skills: this.state.candidates.concat(response.data.skills)
        });
      })
      .catch(err => console.log(err));*/
    let response = {
      skills:["Java","MongoDB","Kafka","React"]
    };
    this.setState({
      skills: this.state.skills.concat(response.skills)
    });
  }

  handleOptionChange = (changeEvent) => {
    console.log(changeEvent.target.row)
    this.setState({
      selectedOption: changeEvent.target.value
    });
  }

  submitForm = (changeEvent) => {
    /* axios
      .get(url, data)
      .then(response => {
        console.log("response is..........", response.data);
        this.setState({
          positions: this.state.positions.concat(response.data)
        });
      })
      .catch(err => console.log(err));*/
  }

  render() {
    let skills = this.state.skills;
    let question_details = skills.map(skills =>{
      return (
          <tr>
            <td width="40%">{skills}</td>
            <td width="60%">
              <label>
                <input type="radio" 
                value="1" 
                checked={this.state.selectedOption === '1'}
                onChange={this.handleOptionChange} 
                />
                1
              </label>
              <label>
                <input type="radio" 
                value="2" 
                checked={this.state.selectedOption === '2'}
                onChange={this.handleOptionChange} 
                />
                2
              </label>
              <label>
                <input type="radio" 
                value="3" 
                checked={this.state.selectedOption === '3'}
                onChange={this.handleOptionChange} />
                3
              </label>
              <label>
                <input type="radio" 
                value="4" 
                checked={this.state.selectedOption === '4'}
                onChange={this.handleOptionChange} />
                4
              </label>
              <label>
                <input type="radio" 
                value="5" 
                checked={this.state.selectedOption === '5'}
                onChange={this.handleOptionChange} />
                5
              </label></td>
          </tr>
        );
      });

    // let details_pending = this.state.pending.map(pending => {
    //   return (
    //     <Table bordered>
    //       <tr>
    //         <td width="50%">{pending.name}</td>
    //         <td width="50%">{pending.email}</td>
    //       </tr>
    //     </Table>
    //   );
    // });
    let redirectVar = null;
    /* if (localStorage.getItem("name") == null) {
          redirectVar = <Redirect to="/" />;
        }*/
    return (
      <div>
        {" "}
        {redirectVar}
        <NavBar />
        <br />
        <br />
        <br />
        <br />
        <Table borderless>
          <tr>
            <td>
              <ListGroup variant="flush" style={group}>
                <ListGroup.Item action href="./homeInterviewer">
                  <p style={para}> Home </p>
                </ListGroup.Item>
              </ListGroup>
            </td>
            <td style={para} width="700">
        <Table bordered>
          <tr>
            <td width="100%">Please rate the candidate on the basis of following skills:
            <br/>Rate from 1-5
            <br/>1 -> Not applicable
            <br/>2 -> Does not meet requirement
            <br/>3 -> Can Learn with Training
            <br/>4 -> Meets Requirements
            <br/>5 -> Exceeds Expectations</td>
          </tr>
        </Table>
        <Table bordered>
          {question_details}
        </Table>
        <Button
                  variant="light"
                  style={btn}
                  onClick={this.submitForm}
                >
                  Submit Form
                </Button>
            </td>
          </tr>
        </Table>
      </div>
    );
  }
}
const group = {
  width: 320,
  height: 700
};
const para = {
  fontfamily: "Arial, Helvetica, sans-serif",
  fontSize: 30,
  textAlign: "center",
  padding: 10,
  margin: 10,
  display: "inline-block",
  verticalAllign: "middle"
};
const para1 = {
  fontfamily: "Arial, Helvetica, sans-serif",
  fontSize: 20,
  textAlign: "center",
  padding: 10,
  margin: 10,

  verticalAllign: "middle"
};
const tabletext = {
  textAlign: 'right', 
  alignSelf: 'stretch'
};
const btn = {
  color: "#bd0d39",

  fontFamily: "Arial, Helvetica, sans-serif",
  fontStyle: "normal"
};
export default GetQuestions;
