import React, { Component } from "react";
import FontAwesome from 'react-fontawesome'
import Dropdown from './DropDown.jsx'

import {
  MDBContainer,
  MDBNavbar,
  MDBNavbarBrand,
  MDBNavbarNav,
  MDBNavbarToggler,
  MDBCollapse,
  MDBNavItem,
  MDBNavLink,
  MDBIcon
} from "mdbreact";
import { BrowserRouter as Router } from "react-router-dom";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import GoogleLogin from 'react-google-login';
import "mdbreact/dist/css/mdb.css";

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listOpen: false,
    headerTitle: this.props.title,
      collapse: false,
      isSignedIn: false,
      role: [
          {
              id: 0,
              title: 'Admin',
              selected: false,
              key: 'role'
          },
          {
            id: 1,
            title: 'Interviewer',
            selected: false,
            key: 'role'
          },
          {
            id: 2,
            title: 'Candidate',
            selected: false,
            key: 'role'
          }
        ]

    };
    this.onClick = this.onClick.bind(this);
  }

  onClick() {
    this.setState({
      collapse: !this.state.collapse
    });
  }

  onSuccess() {
  this.setState({
    isSignedIn: true
    })
  }

  handleClickOutside(){
  this.setState({
    listOpen: false
    })
  }

  login(response) {
    console.log("Google Response: "+JSON.stringify(response));
  }

  resetThenSet = (id, key) => {
    let temp = JSON.parse(JSON.stringify(this.state[key]))
    temp.forEach(item => item.selected = false);
    temp[id].selected = true;
    this.setState({
      [key]: temp
    });
  }

  render() {
    const{list} = this.props
    const{listOpen, headerTitle} = this.state
    const responseGoogle = (response) => {
      if(!response.error)
        this.login(response)
      //call Login API with{
        //"name":"response.name",
        //"email":"response.email"
        //"company":"",
        //"usertype":""}

    }
    const bgPink = { backgroundColor: "#e91e63" };
    const container = { height: 1300 };
    return (
      <div>
        <Router>
          <header>
            <MDBNavbar style={bgPink} dark expand="md" scrolling fixed="top">
              <MDBNavbarBrand href="/">
                <strong>Feed-Me-Back</strong>
              </MDBNavbarBrand>
              <MDBNavbarToggler onClick={this.onClick} />
              <MDBCollapse isOpen={this.state.collapse} navbar>
                <MDBNavbarNav right>
                  <MDBNavItem>
                    <MDBNavLink to="/">Features</MDBNavLink>
                  </MDBNavItem>
                  <MDBNavItem>
                    <MDBNavLink to="./integration">Integration</MDBNavLink>
                  </MDBNavItem>
                  <MDBNavItem active>
                    <MDBNavLink to="/login">Login</MDBNavLink>
                  </MDBNavItem>
                </MDBNavbarNav>
              </MDBCollapse>
            </MDBNavbar>
          </header>
        </Router>

        <table>
          <tr>
            <td>
              <img
                src={require("./login1.jpg")}
                width={1000}
                class="rounded"
                alt="avatar"
              />
            </td>
            <td>
              <h3>Login with Google</h3>
              <form>
                <Dropdown
                  title="Login as"
                  list={this.state.role}
                  resetThenSet={this.resetThenSet}
                />

                <div className="company">
                  <input type="text"
                    placeholder="Company" 
                    type="text" 
                    name="company" 
                    noValidate/>
                </div>

                <div className="App">
                  <GoogleLogin
                    clientId="873380339585-lp7se9eau76buen9oa0787e285tpr42k.apps.googleusercontent.com" //CLIENTID NOT CREATED YET
                    buttonText="LOGIN WITH GOOGLE"
                    onSuccess={responseGoogle}
                    onFailure={responseGoogle}
                  />
                </div>
              </form>
            </td>
          </tr>
        </table>
      </div>
    );
  }
}
const para = {
  fontfamily: "'Helvetica Neue', Helvetica, Arial, sans-serif"
};
const btn = {
  color: "#bd0d39",

  fontFamily: "Arial, Helvetica, sans-serif",
  fontStyle: "normal"
};
export default Login;
